package chap02;

public class VariableExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int value = 10;
		
		//변수 value값을 읽고 10을 더하는 산술 연산을 수행
	   //연산의 결과값을 변수 result의 초기값으로 저장
		int result = value + 10;
		
		System.out.println(result);

	}

}
